import React, { Component } from "react";
import axios from "axios";
import AddIcon from "@material-ui/icons/Add";

export default class CreateArea extends Component {
  constructor(props) {
    super(props);

    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeContent = this.onChangeContent.bind(this);
    this.handleExpandedClick = this.handleExpandedClick.bind(this);
    this.handleNotExpandedClick = this.handleNotExpandedClick.bind(this);

    this.state = {
      title: "",
      content: "",
      isExpanded: false,
    };
  }

  handleExpandedClick() {
    this.setState({ isExpanded: true });
  }

  handleNotExpandedClick() {
    this.setState({ isExpanded: false });
  }

  onChangeTitle(e) {
    this.setState({
      title: e.target.value,
    });
  }

  onChangeContent(e) {
    this.setState({
      content: e.target.value,
    });
  }

  onSubmit(e) {
    e.preventDefault();

    const newListItem = {
      title: this.state.title,
      content: this.state.content,
    };

    console.log(newListItem);

    axios
      .post("http://localhost:5000/list/add", newListItem)
      .then((res) => console.log(res.data));

    window.location = "/";
  }

  isExpanded() {
    this.setState({ isExpanded: true });
  }

  render() {
    const isExpanded = this.state.isExpanded;
    return (
      <div>
        <form>
          {isExpanded ? (
            <input
              onChange={this.onChangeTitle}
              name="title"
              placeholder="Title"
              value={this.state.title}
            />
          ) : null}

          <textarea
            onChange={this.onChangeContent}
            name="content"
            placeholder="Take a note..."
            rows="3"
            value={this.state.content}
            onClick={this.handleExpandedClick}
          />

          <button onClick={this.onSubmit.bind(this)}>
            <span>
              <AddIcon />
            </span>
          </button>
        </form>
      </div>
    );
  }
}

