import CreateArea from "./CreateArea";
import Note from "./Note";

export default class NoteDashboard extends Component {
  constructor(props) {
    super(props);

    this.state = {};
  }

  render() {
    return (
      <div>
        <Header />
        <CreateArea />
        {notes.map((note, index) => {
          return (
            <Note
              title={note.title}
              content={note.content}
              key={index}
              id={index}
            />
          );
        })}
        <Footer />
      </div>
    );
  }
}

