import React, { useState } from "react";
import axios from "axios";
import Popup from "reactjs-popup";
import DeleteIcon from "@material-ui/icons/Delete";
import { TextField, Button } from "@material-ui/core";

function Note(props) {
  const [editNote, setEditNote] = useState({
    title: "",
    content: "",
  });
  function handleOnDelete() {
    const userId = props.userId;
    const noteId = props.noteId;
    axios
      .delete("/user/delete/" + userId + "/" + noteId)
      .then((response) => {
        console.log(response);
      })
      .catch((err) => console.log(err));
    props.deleteNote(props.index);
  }
  function testFunction() {
    console.log("hello");
  }
  return (
    <div className="note">
      <h2>{props.title}</h2>
      <p>{props.content}</p>
      <button onClick={handleOnDelete}>
        <DeleteIcon />
      </button>
      <Popup trigger={<button>Edit</button>}>
        <div>
          <label>Title</label>
          <TextField fullWidth label={props.title} name="editTitle" />
          <label>Content</label>
          <TextField fullWidth label={props.content} name="editContent" />
          <Button onClick={testFunction}>Submit</Button>
        </div>
      </Popup>
    </div>
  );
}

export default Note;
