import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";
import Header from "./Header";
import Footer from "./Footer";
import CreateArea from "./CreateArea";
import Note from "./Note";

function NoteDashboard(props) {
  const location = useLocation();
  const id = location.state.userId;
  const [users, setUsers] = useState([]);
  const [currentNote, setCurrentNote] = useState([
    {
      title: "",
      content: "",
    },
  ]);

  // Gets user information from db
  useEffect(() => {
    setTimeout(() => {
      axios.get("http://localhost:5000/user/").then((response) => {
        const currentUser = response.data.find((user) => user._id === id);
        const currentTodoList = currentUser.todoList;
        setCurrentNote(currentTodoList);
      }, 1000);
    });
  }, []);

  return (
    <div>
      <Header />
      <CreateArea />
      {users.map((user, index) => {
        return (
          <Note
            key={index}
            noteId={user.todoList._id}
            title={user.todoList.title}
            content={user.todoList.content}
            // onDelete={deleteNote}
          />
        );
      })}
      <Footer />
    </div>
  );
}

export default NoteDashboard;

// Receives data from backend
// Equivilent to componendDidMount componentDidUpdate
