import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import Header from "./Header";
import Footer from "./Footer";
import axios from "axios";

//TODO
// Compare loginUser with data base username and password
// if(exits) ==> redirect to NoteDashboard
// else ==> print(Please sign up )

function Home(props) {
  const history = useHistory();

  const [users, setUsers] = useState([]);
  const [userInput, setUserInput] = useState({
    username: "",
    password: "",
  });

  // Get's list of users from db
  useEffect(() => {
    setTimeout(() => {
      axios.get("http://localhost:5000/user").then((response) => {
        setUsers([...response.data]);
      }, 1000);
    });
  }, []);

  function handleClick() {
    history.push("./create");
  }

  function handleChange(e) {
    const { name, value } = e.target;

    setUserInput((prevInfo) => {
      return {
        ...prevInfo,
        [name]: value,
      };
    });
  }

  // Checking to see if current user's log info exists in db
  function logIn(e) {
    e.preventDefault();
    const currentUser = users.find(
      (users) => users.username === userInput.username
    );
    if (!currentUser) {
      console.log("The Username was not found. Please try again");
    } else {
      if (currentUser.password === userInput.password) {
        history.push({
          pathname: "./note",
          state: { userId: currentUser._id },
        });
      } else {
        console.log("The password is incorrect");
      }
    }
  }

  return (
    <div>
      <Header />
      <h1>Welcome to Andy's Keeper App</h1>
      <h2>Log In...</h2>
      <form>
        <input
          name="username"
          placeholder="Enter your Username"
          onChange={handleChange}
        />
        <input
          name="password"
          placeholder="Enter your Password"
          onChange={handleChange}
        />
        <button onClick={handleClick}>Create Account</button>
        <button onClick={logIn}>Log In</button>
      </form>
      <Footer />
    </div>
  );
}

export default Home;
